package level2;

import java.util.*;

public class level2_5_10869 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner sc = new Scanner(System.in);
		
		System.out.println("두 개의 수를 입력하세요 : ");
		
		int num1 = sc.nextInt();
		int num2 = sc.nextInt();
		
		System.out.println("A+B="+(num1+num2));
		System.out.println("A-B="+(num1-num2));
		System.out.println("A*B="+(num1*num2));
		System.out.println("A/B="+(num1/num2));
		System.out.println("A%B="+(num1%num2));

	}

}
